package client.Model;
public class RoadPiece {

	private int key;
	private int[] adjacentRoadsA;
	private int[] adjacentRoadsB;

	public RoadPiece(int key, int[] adjacentRoadsA, int[] adjacentRoadsB) {
		this.key = key;
		this.adjacentRoadsA = adjacentRoadsA;
		this.adjacentRoadsB = adjacentRoadsB;
	}

	public int[] getOppositeDirectionRoads(int roadKey) {
		if (roadKey < 0) {
			return getAllAdjacentRoads();
		}

		for (int i = 0; i < this.adjacentRoadsA.length; i++) {
			if (this.adjacentRoadsA[i] == roadKey) {
				return this.adjacentRoadsB;
			}
		}
		return this.adjacentRoadsA;
	}

	public int[] getAllAdjacentRoads() {
		int[] allAdjacentRoads = new int[adjacentRoadsA.length
				+ adjacentRoadsB.length];
		int index = 0;

		for (int i = 0; i < this.adjacentRoadsA.length; i++) {
			allAdjacentRoads[index] = this.adjacentRoadsA[i];
			index++;
		}

		for (int i = 0; i < this.adjacentRoadsB.length; i++) {
			allAdjacentRoads[index] = this.adjacentRoadsB[i];
			index++;
		}

		return allAdjacentRoads;
	}

	@Override
	public String toString() {
		StringBuilder sb = new StringBuilder("");
		sb.append("( ");
		sb.append(key);
		sb.append(" ( ");
		for (int i = 0; i < this.adjacentRoadsA.length; i++) {
			sb.append(this.adjacentRoadsA[i]);
			sb.append(" ");
		}
		sb.append(") ");
		sb.append(" ( ");
		for (int i = 0; i < this.adjacentRoadsB.length; i++) {
			sb.append(this.adjacentRoadsB[i]);
			sb.append(" ");
		}
		sb.append(") ");
		sb.append(" ) ");
		return sb.toString();
	}

	@Override
	public boolean equals(Object o) {
		if (this.getClass() != o.getClass()) {
			return false;
		}
		return this.toString().equals(((RoadPiece) o).toString());
	}

	public int[] getAdjacentRoadsA() {
		return adjacentRoadsA;
	}

	public int[] getAdjacentRoadsB() {
		return adjacentRoadsB;
	}
	
	public void setAdjacentRoadsA(int[] adjacentRoadsA) {
		this.adjacentRoadsA = adjacentRoadsA;	
	}

	public void setAdjacentRoadsB(int[] adjacentRoadsB) {
		this.adjacentRoadsB = adjacentRoadsB;	
	}

	//TODO remove?
	// public int getRoadValue() {
	// return this.key;
	// }

}

package client.Model;
public class Road {
	private int key;
	private int[] adjacentRoadsA;
	private int[] adjacentRoadsB;

	public Road(int key, int[] adjacentRoadsA, int[] adjacentRoadsB) {
		this.key = key;
		this.adjacentRoadsA = adjacentRoadsA;
		this.adjacentRoadsB = adjacentRoadsB;
	}
	
	public int[] getAdjacentRoadsA() {
		return this.adjacentRoadsA;
	}
	
	public int[] getAdjacentRoadsB() {
		return this.adjacentRoadsB;
	}

	@Override
	public String toString() {
		StringBuilder sb = new StringBuilder("");
		sb.append("( ");
		sb.append(key);
		sb.append(" ( ");
		for (int i = 0; i < this.adjacentRoadsA.length; i++) {
			sb.append(this.adjacentRoadsA[i]);
			sb.append(" ");
		}
		sb.append(") ");
		sb.append(" ( ");
		for (int i = 0; i < this.adjacentRoadsB.length; i++) {
			sb.append(this.adjacentRoadsB[i]);
			sb.append(" ");
		}
		sb.append(") ");
		sb.append(" ) ");
		return sb.toString();
	}

	@Override
	public boolean equals(Object o) {
		if (this.getClass() != o.getClass()) {
			return false;
		}
		return this.toString().equals(((Road) o).toString());
	}

}

package client.Model;

/**
 * this class manages all of the individual player's data.
 * 
 * @author Andrew Davidson. Created May 28, 2010.
 */
public class Player {
	// TODO implement an enumeration for resource cards
	// TODO add port and bank trade rates
	private int wheatCount;
	private int woodCount;
	private int woolCount;
	private int brickCount;
	private int oreCount;
	private int playedSoldierCount;
	private int victoryPoints;
	private int totalResourceCards;
	//TODO remove or implement
//	private int playerIndex;
//	private int[] unplayedDevCardCount;
//	private Color color;

	/**
	 * returns all of the player's cards in a convenient array.
	 * 
	 * @return array of player's cards
	 */
	public int[] getCards() {
		int[] cards = { this.wheatCount, this.woodCount, this.woolCount,
				this.brickCount, this.oreCount, this.playedSoldierCount,
				this.totalResourceCards };
		return cards;
	}

	/**
	 * adjusts, but does NOT replace the old number of cards the player holds.
	 * 
	 * @param delta
	 *            - the number of cards to add or remove from the player's hand
	 */
	public void adjustCards(int[] delta) {
		this.wheatCount += delta[0];
		this.woodCount += delta[1];
		this.woolCount += delta[2];
		this.brickCount += delta[3];
		this.oreCount += delta[4];
		this.playedSoldierCount += delta[5];
		this.updateTotal();
	}

	/**
	 * calculates the total number of cards that the player holds, primarily to
	 * determine how many cards will be lost if a seven is rolled.
	 * 
	 */
	private void updateTotal() {
		this.totalResourceCards = this.wheatCount + this.woodCount + this.woolCount
				+ this.brickCount + this.oreCount;
	}

	/**
	 * tells panel how many victory points the player has. The player wins at
	 * 10.
	 * 
	 * @return number of victory points
	 */
	public int getVPs() {
		return this.victoryPoints;
	}
}

