package client.Controller;

import java.awt.Dimension;
import java.awt.FlowLayout;
import javax.swing.JFrame;
import client.GUI.BoardRenderer;
import client.GUI.UserPanel;

/**
 * creates the frame, the board, and the user panel.
 * 
 * @author Andrew Davidson. Created May 7, 2010.
 */

public class Main {

	private static final Dimension SIZE = new Dimension(1200, 900);

	/**
	 * Starts the program.
	 * 
	 * @param args
	 *            Command-line arguments, ignored here
	 */
	public static void main(String[] args) {
		JFrame frame = new JFrame();
		frame.setSize(SIZE);
		frame.setTitle("Settlers of Catan");
		frame.setLayout(new FlowLayout());
		Game game = new Game();
		UserPanel myPanel = new UserPanel(game);
		frame.add(myPanel, FlowLayout.LEFT);
		BoardRenderer myBoard = new BoardRenderer(game);
		frame.add(myBoard, FlowLayout.CENTER);
		frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
		frame.setVisible(true);
	}
}

package client.Controller;

import client.GUI.HexComponent;
import client.GUI.HexComponent.RoadPosition;

public class LocationKey {

	private int hexIndex;
	private RoadPosition pos;

	public LocationKey(int hexIndex, HexComponent.RoadPosition pos) {
		this.hexIndex = hexIndex;
		this.pos = pos;
	}

	public int getHexIndex() {
		return this.hexIndex;
	}

	@Override
	public boolean equals(Object o) {
		if (o.getClass() != this.getClass()) {
			return false;
		}
		if (((LocationKey) o).getHexIndex() != this.hexIndex) {
			return false;
		}
		if (((LocationKey) o).getRoadPosition() != this.pos) {
			return false;
		}
		return true;
	}

	private RoadPosition getRoadPosition() {
		return this.pos;
	}

	@Override
	public int hashCode() {
		return this.hexIndex * 40 + this.pos.hashCode();
	}
}

package client.Controller;

import java.util.HashMap;
import client.GUI.HexComponent;

public class HexManager {

	public HashMap<LocationKey, Integer> roadMap;
	public HashMap<StructureLocationKey, Integer> structureMap;

	public HexManager() {

		initializeRoadMap();
		initializeStructureMap();

	}

	public int getRoadId(int hexIndex, HexComponent.RoadPosition pos) {
		return this.roadMap.get(new LocationKey(hexIndex, pos));
	}

	public int getStructureId(int hexIndex, HexComponent.StructurePosition pos) {
		return -1;
	}

